contracts/AddressRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/utils/introspection/IERC165.sol";

contract AddressRegistry {
    // address of the owner
    address public immutable owner;

    // address of the marketplace
    address public marketplace;

    //address of the auction
    address public auction;

    // address of the platform nft container
    address public nft;

    // address of the nft factory
    address public nftFactory;

    // address of weth token
    address public WETH;

    /// @notice to check if is valid nft contract
    bytes4 private constant INTERFACE_ID_ERC721 = 0x80ac58cd;

    modifier onlyOwner() {
        require(msg.sender == owner, "not contract owner");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function updateMarketplace(address _marketplace) public onlyOwner {
        marketplace = _marketplace;
    }

    function updateAuction(address _auction) public onlyOwner {
        auction = _auction;
    }

    function updateNft(address _nft) public onlyOwner {
        require(
            IERC165(_nft).supportsInterface(INTERFACE_ID_ERC721),
            "invalid nft address"
        );
        nft = _nft;
    }

    function updateNftFactory(address _nftFactory) public onlyOwner {
        nftFactory = _nftFactory;
    }

    function updateWETH(address _WETH) public onlyOwner {
        WETH = _WETH;
    }
}

contracts/Auction.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";

interface IAddressRegistry {
    function marketplace() external view returns (address);
}

interface IMarketplace {
    function listedItems(
        address _nftAddress,
        uint256 _tokenId
    ) external view returns (address, uint256 price, bool);

    function transferNFTtoAuctionWinner(
        address _nftAddress,
        uint256 _tokenId,
        address _winner
    ) external;
}

contract Auction is ReentrancyGuard {
    // events
    event AuctionCreated(
        address indexed nft,
        uint256 indexed tokenId,
        uint256 minBid,
        uint256 startTime,
        uint256 endTime
    );

    event AuctionCanceled(address indexed nft, uint256 indexed tokenId);

    event UpdatedPlatformFee(uint256 platformFee);

    event UpdatedPlatformFeeRecipient(address recipient);
    event BidPlaced(
        address indexed nft,
        uint256 indexed tokenId,
        address indexed bidder,
        uint256 bid
    );

    event BidRefunded(
        address indexed nft,
        uint256 indexed tokenId,
        address indexed bidder,
        uint256 bid
    );

    event BidWithdrawn(
        address indexed nft,
        uint256 indexed tokenId,
        address indexed bidder,
        uint256 bid
    );

    event AuctionResulted(
        address oldOwner,
        address indexed nft,
        uint256 indexed tokenId,
        address indexed winner,
        uint256 winingPrice
    );

    event PauseToggled(bool isPaused);

    event UpdatedMinBid(
        address indexed nft,
        uint256 indexed tokenId,
        uint256 newMinBid
    );

    event UpdatedStartTime(
        address indexed nft,
        uint256 indexed tokenId,
        uint256 newStartTime
    );

    event UpdatedEndTime(
        address indexed nft,
        uint256 indexed tokenId,
        uint256 newEndTime
    );

    // structs
    struct AuctionStructure {
        address payable owner;
        uint256 minBid;
        uint256 startTime;
        uint256 endTime;
        bool resulted;
    }
    struct HighestBid {
        address payable bidder;
        uint256 bid;
        uint256 lastBidTime;
    }

    /// @notice owner of the contract
    address public immutable owner;

    /// @notice platform fee recipient
    address payable public platformFeeRecipient;

    /// @notice platform fee percentage
    uint256 public platformFee;

    /// @notice pause variable for pausing the contract
    bool public isPaused;

    ///@notice address registry
    IAddressRegistry public addressRegistry;

    // mappings
    /// @notice nft address -> token id -> auction
    mapping(address => mapping(uint256 => AuctionStructure)) public auctions;

    /// @notice nft address -> token id -> highest bid
    mapping(address => mapping(uint256 => HighestBid)) public highestBids;

    // modifiers

    modifier onlyOwner() {
        require(msg.sender == owner, "not owner");
        _;
    }
    modifier whenNotPaused() {
        require(!isPaused, "contract is paused");
        _;
    }

    modifier isNftOwner(address _nftAddress, uint256 _tokenId) {
        IERC721 nft = IERC721(_nftAddress);
        require(nft.ownerOf(_tokenId) == msg.sender, "not the token owner");
        _;
    }

    modifier auctionNotExist(address _nftAddress, uint256 _tokenId) {
        require(
            auctions[_nftAddress][_tokenId].endTime == 0,
            "auction already exist"
        );
        _;
    }

    modifier auctionExist(address _nftAddress, uint256 _tokenId) {
        require(
            auctions[_nftAddress][_tokenId].endTime > 0,
            "auction not exist"
        );
        _;
    }

    constructor(uint256 _platformFee, address payable _feeRecipient) {
        platformFeeRecipient = _feeRecipient;
        platformFee = _platformFee;
        owner = msg.sender;
    }

    /**
 @notice method for creating new auction
 @dev only when not paused
 @dev only if auction not exist
 @dev only if listed
 @param _nftAddress nft contract address
 @param _tokenId nft token id
 @param _minBid minimum bid required
 @param _startTime time to start the auction
 @param _endTime time to end the auction
*/
    function createAuction(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _minBid,
        uint256 _startTime,
        uint256 _endTime
    )
        external
        whenNotPaused
        isNftOwner(_nftAddress, _tokenId)
        auctionNotExist(_nftAddress, _tokenId)
    {
        IMarketplace marketplace = IMarketplace(addressRegistry.marketplace());
        (, uint256 price, ) = marketplace.listedItems(_nftAddress, _tokenId);
        require(price > 0, "item is not listed");
        require(_startTime > _getNow(), "invalid time start");
        require(
            _endTime >= _startTime + 300,
            "end time should be more than 5 mins"
        );

        auctions[_nftAddress][_tokenId] = AuctionStructure({
            owner: payable(msg.sender),
            minBid: _minBid,
            startTime: _startTime,
            endTime: _endTime,
            resulted: false
        });

        emit AuctionCreated(
            _nftAddress,
            _tokenId,
            _minBid,
            _startTime,
            _endTime
        );
    }

    /**
 @notice method for canceling auction
 @dev only when not paused
 @dev only if exist
 @param _nftAddress nft contract address
 @param _tokenId nft token id
*/
    function cancelAuction(
        address _nftAddress,
        uint256 _tokenId
    ) external nonReentrant auctionExist(_nftAddress, _tokenId) {
        AuctionStructure memory auction = auctions[_nftAddress][_tokenId];
        require(
            IERC721(_nftAddress).ownerOf(_tokenId) == msg.sender &&
                auction.owner == msg.sender,
            "not nft owner"
        );
        require(!auction.resulted, "auction already resulted");
        HighestBid memory highestBid = highestBids[_nftAddress][_tokenId];
        if (highestBid.bidder != address(0)) {
            _refundHighestBidder(
                _nftAddress,
                _tokenId,
                highestBid.bidder,
                highestBid.bid
            );

            // clear highest bidder
            delete (highestBids[_nftAddress][_tokenId]);
        }

        // clear auction
        delete (auctions[_nftAddress][_tokenId]);

        emit AuctionCanceled(_nftAddress, _tokenId);
    }

    /**
 @notice method for placing a bid
 @dev only when not paused
 @dev only if exist
 @param _nftAddress nft contract address
 @param _tokenId nft token id
*/
    function placeBid(
        address _nftAddress,
        uint256 _tokenId
    )
        external
        payable
        nonReentrant
        whenNotPaused
        auctionExist(_nftAddress, _tokenId)
    {
        AuctionStructure memory auction = auctions[_nftAddress][_tokenId];
        HighestBid storage highestBid = highestBids[_nftAddress][_tokenId];
        require(
            _getNow() < auction.endTime && _getNow() >= auction.startTime,
            "out of time"
        );
        require(!auction.resulted, "auction has resulted");
        require(msg.value >= auction.minBid, "bid is less than minimum bid");
        require(msg.value > highestBid.bid, "failed to outBid highest bidder");
        if (highestBid.bidder != address(0)) {
            _refundHighestBidder(
                _nftAddress,
                _tokenId,
                highestBid.bidder,
                highestBid.bid
            );
        }

        highestBid.bid = msg.value;
        highestBid.bidder = payable(msg.sender);
        highestBid.lastBidTime = _getNow();

        emit BidPlaced(_nftAddress, _tokenId, msg.sender, msg.value);
    }

    /**
 @notice method for withdraw bid by highest bidder after 12 hours
 @dev only highest bidder
 @param _nftAddress nft contract address
 @param _tokenId nft token id
*/
    function withdrawBid(
        address _nftAddress,
        uint256 _tokenId
    ) external nonReentrant {
        HighestBid memory highestBid = highestBids[_nftAddress][_tokenId];
        require(
            msg.sender == highestBid.bidder,
            "you are not the highest bidder"
        );
        uint256 _endTime = auctions[_nftAddress][_tokenId].endTime;
        require(
            _getNow() > _endTime && _getNow() - _endTime >= 43200,
            "can withdraw only after 12 hours auction has ended"
        );

        uint256 previousBid = highestBid.bid;

        //cleaning the existing top bidder
        delete (highestBids[_nftAddress][_tokenId]);

        // refunding the top bidder
        _refundHighestBidder(
            _nftAddress,
            _tokenId,
            payable(msg.sender),
            previousBid
        );

        emit BidWithdrawn(_nftAddress, _tokenId, msg.sender, previousBid);
    }

    /**
 @notice method for resulting the finished auction
 @dev only nft owner
 @dev only if there is a bidder
 @param _nftAddress nft contract address
 @param _tokenId nft token id
*/
    function resultAuction(
        address _nftAddress,
        uint256 _tokenId
    )
        external
        nonReentrant
        auctionExist(_nftAddress, _tokenId)
        isNftOwner(_nftAddress, _tokenId)
    {
        AuctionStructure storage auction = auctions[_nftAddress][_tokenId];
        // ensure caller is owner
        require(msg.sender == auction.owner, "not owner");
        // ensure auction is ended
        require(_getNow() > auction.endTime, "auction not ended");
        // ensure auction has not resulted
        require(!auction.resulted, "auction already resulted");

        // checking highest bidder info
        HighestBid memory highestBid = highestBids[_nftAddress][_tokenId];
        address winner = highestBid.bidder;
        uint256 winningBid = highestBid.bid;

        // if there is no bids delete the auction
        if (winner == address(0)) {
            delete (auctions[_nftAddress][_tokenId]);
            revert("there is no bidder");
        }

        // ensure value is higher than minimum bid
        require(winningBid >= auction.minBid, "amount lower than minimum bid");

        auction.resulted = true;

        delete (highestBids[_nftAddress][_tokenId]);

        _resultAuction(
            _nftAddress,
            _tokenId,
            auction.owner,
            winner,
            winningBid
        );
    }

    /**
 @notice method for updating minimum bid
 @dev only if exist
 @dev only auction owner
 @param _nftAddress nft contract address
 @param _tokenId nft token id
 @param _newMinBid new minimum bid
*/
    function updateMinBid(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _newMinBid
    ) external auctionExist(_nftAddress, _tokenId) {
        AuctionStructure storage auction = auctions[_nftAddress][_tokenId];
        require(msg.sender == auction.owner, "not auction owner");
        require(!auction.resulted, "auction already resulted");

        HighestBid memory highestBid = highestBids[_nftAddress][_tokenId];
        // check if there is highest bid
        require(
            highestBid.bid == 0,
            "cannot update the minimum bid if bidder exist"
        );

        auction.minBid = _newMinBid;

        emit UpdatedMinBid(_nftAddress, _tokenId, _newMinBid);
    }

    /**
 @notice method for updating start time
 @dev only if exist
 @dev only auction owner
 @param _nftAddress nft contract address
 @param _tokenId nft token id
 @param _newStartTime new start time
*/
    function updateStartTime(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _newStartTime
    ) external auctionExist(_nftAddress, _tokenId) {
        AuctionStructure storage auction = auctions[_nftAddress][_tokenId];
        require(msg.sender == auction.owner, "not auction owner");
        require(!auction.resulted, "auction already resulted");
        require(_newStartTime > 0, "invalid start time");
        require(auction.startTime + 60 > _getNow(), "auction already started");
        require(
            _newStartTime + 300 < auction.endTime,
            "auction start time should be less than end time by 5 min"
        );

        auction.startTime = _newStartTime;
        emit UpdatedStartTime(_nftAddress, _tokenId, _newStartTime);
    }

    /**
 @notice method for updating end time
 @dev only if exist
 @dev only auction owner
 @param _nftAddress nft contract address
 @param _tokenId nft token id
 @param _newEndTime new start time
*/
    function updateEndTime(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _newEndTime
    ) external auctionExist(_nftAddress, _tokenId) {
        AuctionStructure storage auction = auctions[_nftAddress][_tokenId];
        require(msg.sender == auction.owner, "not auction owner");
        require(!auction.resulted, "auction already resulted");
        require(_newEndTime > 0, "invalid end time");
        require(_getNow() < auction.endTime, "auction is ended");
        require(
            _newEndTime > auction.startTime + 300,
            "auction end time should be greater than start time by 5 mins"
        );
        require(
            _newEndTime > _getNow() + 300,
            "end time should be more than 5 mins"
        );

        auction.endTime = _newEndTime;
        emit UpdatedEndTime(_nftAddress, _tokenId, _newEndTime);
    }

    /// @notice updates the platform fee
    /// @dev only admin
    /// @param _platformFee new platform fee to set
    function updatePlatformFee(uint256 _platformFee) public onlyOwner {
        platformFee = _platformFee;
        emit UpdatedPlatformFee(_platformFee);
    }

    /// @notice updates the fee recipient
    /// @dev only admin
    /// @param _feeRecipient new fee recipient to receive platform fee
    function updateFeeRecipient(
        address payable _feeRecipient
    ) public onlyOwner {
        require(_feeRecipient != address(0), "recipient cannot be empty");
        platformFeeRecipient = _feeRecipient;
        emit UpdatedPlatformFeeRecipient(_feeRecipient);
    }

    /**
     @notice update address registry
     @dev only admin
     @param _newAddress new address
     */
    function updateAddressRegistry(address _newAddress) public onlyOwner {
        addressRegistry = IAddressRegistry(_newAddress);
    }

    /**
     @notice Toggling the pause 
     @dev Only admin
     */
    function toggleIsPaused() external onlyOwner {
        isPaused = !isPaused;
        emit PauseToggled(isPaused);
    }

    /// Getter functions

    /**
     @notice Method for getting all info about the auction
     @param _nftAddress ERC 721 Address
     @param _tokenId Token id of the nft
     */
    function getAuction(
        address _nftAddress,
        uint256 _tokenId
    )
        external
        view
        returns (
            address payable auctionOwner,
            uint256 minBid,
            uint256 startTime,
            uint256 endTime,
            bool resulted
        )
    {
        AuctionStructure memory auction = auctions[_nftAddress][_tokenId];

        return (
            auction.owner,
            auction.minBid,
            auction.startTime,
            auction.endTime,
            auction.resulted
        );
    }

    /**
     @notice Method for getting all info about the highest bidder
     @param _nftAddress ERC 721 Address
     @param _tokenId Token id of the nft
     */
    function getHighestBidder(
        address _nftAddress,
        uint256 _tokenId
    )
        external
        view
        returns (address payable bidder, uint256 bid, uint256 lastBidTime)
    {
        HighestBid memory highestBid = highestBids[_nftAddress][_tokenId];

        return (highestBid.bidder, highestBid.bid, highestBid.lastBidTime);
    }

    /// @notice getting block timestamp
    function _getNow() internal view returns (uint256) {
        return block.timestamp;
    }

    /**
 @notice method for resulting auction
 @param _nftAddress ERC 721 Address
 @param _tokenId nft token id
 @param auctionOwner address of auction owner
 @param winner address of winner
 @param winningBid bid amount
*/
    function _resultAuction(
        address _nftAddress,
        uint256 _tokenId,
        address auctionOwner,
        address winner,
        uint256 winningBid
    ) private {
        uint256 feeAmount;

        // sending fee to platform owner
        feeAmount = (winningBid * platformFee) / 1000;
        (bool successFee, ) = platformFeeRecipient.call{value: feeAmount}("");
        require(successFee, "failed to transfer fee");

        // sending royalty fee to owner if collection supports ERC2981 standard
        if (
            IERC165(_nftAddress).supportsInterface(type(IERC2981).interfaceId)
        ) {
            (address receiver, uint256 royaltyFee) = IERC2981(_nftAddress)
                .royaltyInfo(_tokenId, winningBid);

            (bool royaltySuccess, ) = payable(receiver).call{value: royaltyFee}(
                ""
            );
            require(royaltySuccess, "failed to transfer the royalty");
            feeAmount += royaltyFee;
        }

        // sending bid to auction owner
        (bool successTransfer, ) = auctionOwner.call{
            value: winningBid - feeAmount
        }("");
        require(successTransfer, "failed to transfer the amount to owner");

        // sending nft to winner using marketplace function (because marketplace is approved to move token)
        IMarketplace marketplace = IMarketplace(addressRegistry.marketplace());
        marketplace.transferNFTtoAuctionWinner(_nftAddress, _tokenId, winner);

        emit AuctionResulted(
            msg.sender,
            _nftAddress,
            _tokenId,
            winner,
            winningBid
        );

        delete (auctions[_nftAddress][_tokenId]);
    }

    /**
 @notice method for refunding highest bidder
 @param _nftAddress ERC 721 Address
 @param _tokenId nft token id
 @param _bidder address of bidder 
 @param _bid amount
*/
    function _refundHighestBidder(
        address _nftAddress,
        uint256 _tokenId,
        address payable _bidder,
        uint256 _bid
    ) private {
        (bool successRefund, ) = _bidder.call{value: _bid}("");
        require(successRefund, "failed to refund the highest bidder");

        emit BidRefunded(_nftAddress, _tokenId, _bidder, _bid);
    }
}

contracts/Marketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IAddressRegistry {
    function auction() external view returns (address);

    function WETH() external view returns (address);
}

interface IAuction {
    function auctions(
        address _nftAddress,
        uint256 _tokenId
    ) external view returns (address, uint256, uint256, uint256, bool);
}

contract Marketplace is ReentrancyGuard {
    /// @dev events of the contract
    event ItemListed(
        address indexed owner,
        address indexed nftAddress,
        uint256 tokenId,
        uint256 price
    );

    event UpdatedPlatformFee(uint96 platformFee);
    event UpdatedFeeRecipient(address feeRecipient);

    event ItemCanceled(
        address indexed owner,
        address indexed nftAddress,
        uint256 tokenId
    );
    event ItemUpdated(
        address indexed owner,
        address indexed nftAddress,
        uint256 tokenId,
        uint256 newPrice
    );
    event ItemSold(
        address indexed seller,
        address indexed buyer,
        address indexed nft,
        uint256 tokenId,
        uint256 price
    );
    event OfferCreated(
        address indexed offerer,
        address indexed nft,
        uint256 tokenId,
        uint256 amount
    );

    event OfferCanceled(
        address indexed creator,
        address indexed nft,
        uint256 tokenId
    );

    /// @notice structure for listing each item
    struct Listing {
        address owner;
        uint256 price;
        bool sold;
    }

    /// @notice structure for each offer
    struct Offer {
        uint256 offer; // amount
        uint256 expiration; //expiration time
    }

    /// @notice NFT address -> token id -> owner -> price
    mapping(address => mapping(uint256 => Listing)) public listedItems;
    /// @notice NFT address -> token id -> offerer -> offer
    mapping(address => mapping(uint256 => mapping(address => Offer)))
        public offers;

    /// @notice to check if is valid nft contract
    bytes4 private constant INTERFACE_ID_ERC721 = 0x80ac58cd;

    /// @notice owner of the contract
    address public immutable owner;

    /// @notice platform fee in percentage 1 to 1000 eg 25 = 2.5%
    uint16 public platformFee;

    /// @notice fee recipient
    address payable public feeRecipient;

    ///@notice address registry
    IAddressRegistry public addressRegistry;

    /// @notice modifier to check caller is nft owner
    modifier isNftOwner(address _nftAddress, uint256 _tokenId) {
        IERC721 nft = IERC721(_nftAddress);
        require(nft.ownerOf(_tokenId) == msg.sender, "not the token owner");
        _;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "not owner");
        _;
    }

    /// @notice modifier to check if item is already listed
    modifier notListed(address _nftAddress, uint256 _tokenId) {
        Listing memory listing = listedItems[_nftAddress][_tokenId];
        require(listing.price == 0, "item is already listed");
        _;
    }
    /// @notice modifier to make sure item is listed
    modifier isListed(address _nftAddress, uint256 _tokenId) {
        Listing memory listing = listedItems[_nftAddress][_tokenId];

        require(listing.price > 0, "item is not listed");
        _;
    }

    /// @notice check if offer already exist
    modifier offerExist(
        address _nftAddress,
        uint256 _tokenId,
        address _creator
    ) {
        Offer memory targetOffer = offers[_nftAddress][_tokenId][_creator];
        require(
            targetOffer.offer > 0 || targetOffer.expiration > _getNow(),
            "offer doesn't exist or expired"
        );
        _;
    }

    /// @notice check if offer not exist
    modifier offerNotExist(
        address _nftAddress,
        uint256 _tokenId,
        address _creator
    ) {
        Offer memory targetOffer = offers[_nftAddress][_tokenId][_creator];
        require(targetOffer.offer == 0, "offer already created");
        _;
    }

    /// @notice contract constructor
    constructor(uint16 _platformFee, address payable _feeRecipient) {
        platformFee = _platformFee;
        feeRecipient = _feeRecipient;
        owner = msg.sender;
    }

    /// @notice for listing new items on marketplace
    /// @dev only valid nft contracts
    /// @dev only admin or approved person
    /// @dev only if not listed before
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    /// @param _price price for list it
    function listItem(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _price
    )
        external
        notListed(_nftAddress, _tokenId)
        isNftOwner(_nftAddress, _tokenId)
    {
        try
            IERC165(_nftAddress).supportsInterface(INTERFACE_ID_ERC721)
        returns (bool supported) {
            // Contracts support ERC-165
            require(supported, "not erc 721 contract");
        } catch (bytes memory /*lowLevelData*/) {
            // Contracts doesn't support ERC-165
            revert("doesn't support ERC-165");
        }

        IERC721 nft = IERC721(_nftAddress);
        require(
            nft.isApprovedForAll(msg.sender, address(this)) ||
                nft.getApproved(_tokenId) == address(this),
            "not approved for marketplace"
        );
        require(_price > 0, "price cannot be zero");

        listedItems[_nftAddress][_tokenId] = Listing(msg.sender, _price, false);

        emit ItemListed(msg.sender, _nftAddress, _tokenId, _price);
    }

    ///@notice update the listing price
    /// @dev only admin
    /// @dev only if it's listed for sale
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    /// @param _newPrice new price to update listing
    function updateListing(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _newPrice
    ) public isNftOwner(_nftAddress, _tokenId) isListed(_nftAddress, _tokenId) {
        require(_newPrice > 0, "price cannot be less than zero");
        Listing storage listing = listedItems[_nftAddress][_tokenId];
        listing.price = _newPrice;

        emit ItemUpdated(msg.sender, _nftAddress, _tokenId, _newPrice);
    }

    /// @notice cancel listed nft
    /// @dev only admin
    /// @dev only if it's listed for sale
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    function cancelListing(
        address _nftAddress,
        uint256 _tokenId
    ) public isNftOwner(_nftAddress, _tokenId) isListed(_nftAddress, _tokenId) {
        delete (listedItems[_nftAddress][_tokenId]);
        emit ItemCanceled(msg.sender, _nftAddress, _tokenId);
    }

    /// @notice for buying an item from marketplace
    /// @dev only if it's listed for sale
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    function buyItem(
        address _nftAddress,
        uint256 _tokenId
    ) public payable isListed(_nftAddress, _tokenId) {
        Listing memory listing = listedItems[_nftAddress][_tokenId];

        require(
            msg.value >= listing.price,
            "insufficient funds for buying nft"
        );

        IAuction auction = IAuction(addressRegistry.auction());
        (, , uint256 startTime, , bool result) = auction.auctions(
            _nftAddress,
            _tokenId
        );
        require(
            startTime == 0 || result == true,
            "cannot buy an item when auction is going on"
        );
        // calculate the fee
        uint256 feeAmount;

        feeAmount = (msg.value * platformFee) / 1000;

        (bool feeSuccess, ) = feeRecipient.call{value: feeAmount}("");
        require(feeSuccess, "transfer failed for paying tax");

        // sending royalty fee to owner if collection supports ERC2981 standard
        if (
            IERC165(_nftAddress).supportsInterface(type(IERC2981).interfaceId)
        ) {
            (address receiver, uint256 royaltyFee) = IERC2981(_nftAddress)
                .royaltyInfo(_tokenId, msg.value);

            (bool royaltySuccess, ) = payable(receiver).call{value: royaltyFee}(
                ""
            );
            require(royaltySuccess, "failed to transfer the royalty");
            feeAmount += royaltyFee;
        }

        (bool sellerSuccess, ) = payable(listing.owner).call{
            value: msg.value - feeAmount
        }("");
        require(sellerSuccess, "transfer failed for nft owner");
        delete (listedItems[_nftAddress][_tokenId]);

        IERC721(_nftAddress).safeTransferFrom(
            listing.owner,
            msg.sender,
            _tokenId
        );
        emit ItemSold(
            listing.owner,
            msg.sender,
            _nftAddress,
            _tokenId,
            listing.price
        );
    }

    /// @notice method for offering item
    /// @dev only if it's listed for sale
    /// @dev converted into two functions because of (stack too deep error)
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    /// @param _wethAmount amount in weth token
    /// @param _expiration offers expiration date
    function createOffer(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _wethAmount,
        uint256 _expiration
    )
        public
        isListed(_nftAddress, _tokenId)
        offerNotExist(_nftAddress, _tokenId, msg.sender)
    {
        require(_wethAmount > 0, "your offer cannot be 0");
        require(_expiration > _getNow(), "invalid expiration");
        // check allowance amount for contract to move tokens
        address wethAddress = addressRegistry.WETH();
        require(
            IERC20(wethAddress).allowance(msg.sender, address(this)) >=
                _wethAmount,
            "Insufficient WETH allowance"
        );

        _createOffer(
            _nftAddress,
            _tokenId,
            _wethAmount,
            msg.sender,
            _expiration
        );
    }

    /// @notice method for canceling existing offer
    /// @dev only if offer exist
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    function cancelOffer(
        address _nftAddress,
        uint256 _tokenId
    ) public offerExist(_nftAddress, _tokenId, msg.sender) {
        delete (offers[_nftAddress][_tokenId][msg.sender]);
        emit OfferCanceled(msg.sender, _nftAddress, _tokenId);
    }

    /// @notice method for accepting offer
    /// @dev only nft admin
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    /// @param _creator offer creator address
    function acceptOffer(
        address _nftAddress,
        uint256 _tokenId,
        address _creator
    )
        public
        nonReentrant
        isNftOwner(_nftAddress, _tokenId)
        offerExist(_nftAddress, _tokenId, _creator)
    {
        _acceptOffer(_nftAddress, _tokenId, _creator);
    }

    /// @notice updates the platform fee
    /// @dev only admin
    /// @param _platformFee new platform fee to set
    function updatePlatformFee(uint16 _platformFee) public onlyOwner {
        platformFee = _platformFee;
        emit UpdatedPlatformFee(_platformFee);
    }

    /// @notice updates the fee recipient
    /// @dev only admin
    /// @param _feeRecipient new fee recipient to receive platform fee
    function updateFeeRecipient(
        address payable _feeRecipient
    ) public onlyOwner {
        require(_feeRecipient != address(0), "recipient cannot be empty");

        feeRecipient = _feeRecipient;
        emit UpdatedFeeRecipient(_feeRecipient);
    }

    /**
     @notice update address registry
     @dev only admin
     @param _newAddress new address
     */
    function updateAddressRegistry(address _newAddress) public onlyOwner {
        addressRegistry = IAddressRegistry(_newAddress);
    }

    function _getNow() internal view returns (uint256) {
        return block.timestamp;
    }

    /// @notice method for transfer and cancel nft listing
    /// @dev only auction contract can call this function
    /// @param _nftAddress nft contract address
    /// @param _tokenId nft token id
    /// @param _winner nft token id
    function transferNFTtoAuctionWinner(
        address _nftAddress,
        uint256 _tokenId,
        address _winner
    ) external isListed(_nftAddress, _tokenId) {
        require(
            msg.sender == addressRegistry.auction(),
            "only auction contract can call this"
        );

        // delete listing
        delete (listedItems[_nftAddress][_tokenId]);

        // send nft to auction winner
        IERC721 nft = IERC721(_nftAddress);
        nft.safeTransferFrom(nft.ownerOf(_tokenId), _winner, _tokenId);
    }

    function _acceptOffer(
        address _nftAddress,
        uint256 _tokenId,
        address _creator
    ) private {
        Offer memory targetOffer = offers[_nftAddress][_tokenId][_creator];

        address wethAddress = addressRegistry.WETH();
        uint256 feeAmount;

        feeAmount = (targetOffer.offer * platformFee) / 1000;
        require(
            IERC20(wethAddress).transferFrom(_creator, feeRecipient, feeAmount),
            "WETH transfer failed"
        );

        // sending royalty fee to owner if collection supports ERC2981 standard
        if (
            IERC165(_nftAddress).supportsInterface(type(IERC2981).interfaceId)
        ) {
            (address receiver, uint256 royaltyFee) = IERC2981(_nftAddress)
                .royaltyInfo(_tokenId, targetOffer.offer);

            require(
                IERC20(wethAddress).transferFrom(
                    _creator,
                    receiver,
                    royaltyFee
                ),
                "WETH transfer failed"
            );

            feeAmount += royaltyFee;
        }

        require(
            IERC20(wethAddress).transferFrom(
                _creator,
                msg.sender,
                targetOffer.offer - feeAmount
            ),
            "WETH transfer failed"
        );

        delete (offers[_nftAddress][_tokenId][_creator]);
        delete (listedItems[_nftAddress][_tokenId]);

        IERC721(_nftAddress).safeTransferFrom(msg.sender, _creator, _tokenId);

        emit ItemSold(
            msg.sender,
            _creator,
            _nftAddress,
            _tokenId,
            targetOffer.offer
        );
        emit OfferCanceled(_creator, _nftAddress, _tokenId);
    }

    /// @dev if auction exist offer cannot be created
    function _createOffer(
        address _nftAddress,
        uint256 _tokenId,
        uint256 _wethAmount,
        address _creator,
        uint256 _expiration
    ) private {
        IAuction auction = IAuction(addressRegistry.auction());
        (, , uint256 startTime, , bool result) = auction.auctions(
            _nftAddress,
            _tokenId
        );
        require(
            startTime == 0 || result == true,
            "cannot place an offer when auction is going on"
        );
        offers[_nftAddress][_tokenId][_creator] = Offer(
            _wethAmount,
            _expiration
        );
        emit OfferCreated(msg.sender, _nftAddress, _tokenId, _wethAmount);
    }
}

contracts/NFTCollection.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

/// @dev imports
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";

contract NFTCollection is ERC721URIStorage, ERC2981 {
    /// @dev events of the contract
    event Minted(
        uint256 tokenId,
        string tokenURI,
        address minter,
        string collectionName
    );
    event UpdatedRoyalty(address feeRecipient, uint96 royaltyFee);

    /// @notice owner of the collection
    address public immutable owner;
    /// @notice token id counter
    uint256 public tokenIds;

    /// @dev modifiers of contract
    modifier onlyOwner() {
        require(msg.sender == owner, "not owner");
        _;
    }

    /// @notice contract constructor for initializing new collection
    constructor(
        string memory name,
        string memory symbol,
        uint96 _royaltyFee,
        address payable _feeRecipient,
        address _owner
    ) ERC721(name, symbol) {
        owner = _owner;

        // setting royalty fee on collection with eip 2981 standard
        _setDefaultRoyalty(_feeRecipient, _royaltyFee);
    }

    function mint(string memory _tokenURI) public onlyOwner returns (uint256) {
        require(bytes(_tokenURI).length > 0, "no token uri");

        uint256 newTokenId = tokenIds += 1;
        _safeMint(msg.sender, newTokenId);
        _setTokenURI(newTokenId, _tokenURI);
        emit Minted(newTokenId, _tokenURI, msg.sender, name());
        return newTokenId;
    }

    /**
    @notice updates the royalty status
    @dev only admin
    @param _feeRecipient new fee recipient to receive platform fee
    @param _royaltyFee new royalty fee to set
     */
    function updateRoyalty(
        address _feeRecipient,
        uint96 _royaltyFee
    ) public onlyOwner {
        require(_feeRecipient != address(0), "recipient cannot be empty");
        require(_royaltyFee <= 1000, "royalty fee cannot be more than 10%");

        _setDefaultRoyalty(_feeRecipient, _royaltyFee);

        emit UpdatedRoyalty(_feeRecipient, _royaltyFee);
    }

    /**
     @notice Method for removing the royalty
     */
    function removeRoyalty() public onlyOwner {
        _deleteDefaultRoyalty();
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view override(ERC721, ERC2981) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}

